        double work_arrays[6 * PROBLEM_SIZE];  // Assuming PROBLEM_SIZE is max dimension

        #pragma omp for schedule(static)
        for (int ij = 0; ij < nx * ny; ij++) {
            const int i = ij / ny;
            const int j = ij % ny;
            const int base = (i * ny + j) * nz;
            const int base5 = base * 5;

            double* const u_base = &u[0][0][0][0] + base5;
            double* const rhs_base = &rhs[0][0][0][0] + base5;
            double* const forcing_base = &forcing[0][0][0][0] + base5;

            for (int k = 0; k < nz; k++) {
                const int k5 = k * 5;
                const double inv_u0 = 1.0/u_base[k5];

                // Store in contiguous temporary arrays
                work_arrays[k] = u_base[k5+1] * inv_u0;          // us
                work_arrays[nz + k] = u_base[k5+2] * inv_u0;     // vs
                work_arrays[2*nz + k] = u_base[k5+3] * inv_u0;   // ws
                work_arrays[3*nz + k] = inv_u0;                  // rho_i

                const double u1 = u_base[k5+1];
                const double u2 = u_base[k5+2];
                const double u3 = u_base[k5+3];
                const double sum_sq = 0.5 * (u1*u1 + u2*u2 + u3*u3) * inv_u0;

                work_arrays[4*nz + k] = sum_sq;                  // square
                work_arrays[5*nz + k] = sum_sq * inv_u0;         // qs

                // Write back to global arrays
                us[i][j][k] = work_arrays[k];
                vs[i][j][k] = work_arrays[nz + k];
                ws[i][j][k] = work_arrays[2*nz + k];
                rho_i[i][j][k] = work_arrays[3*nz + k];
                square[i][j][k] = work_arrays[4*nz + k];
                qs[i][j][k] = work_arrays[5*nz + k];

                // Copy forcing terms
                for (int m = 0; m < 5; m++) {
                    rhs_base[k5+m] = forcing_base[k5+m];
                }
            }
        }

        // Phase 2: Compute fluxes
        #pragma omp for schedule(static)
        for (int i = 1; i < nx_m1; i++) {
            const int plane_size = ny_nz5;
            const double* const u_m1 = &u[0][0][0][0] + (i-1) * plane_size;
            const double* const u_0  = &u[0][0][0][0] + i * plane_size;
            const double* const u_p1 = &u[0][0][0][0] + (i+1) * plane_size;
            double* const rhs_i = &rhs[0][0][0][0] + i * plane_size;

            for (int j = 1; j < ny_m1; j++) {
                const int row_size = nz5;
                const int j_offset = j * row_size;

                // Process blocks of the innermost loop
                for (int k = 1; k < nz_m1; k += 4) {
                    const int k_end = (k + 4 < nz_m1) ? k + 4 : nz_m1;

                    for (int kk = k; kk < k_end; kk++) {
