                        const double* const u_cur = &u_0[idx];
                        const double* const u_minus = &u_m1[idx];
                        const double* const u_plus = &u_p1[idx];
                        double* const rhs_cur = &rhs_i[idx];

                        // Pre-compute common expressions
                        const double u_diff0 = u_plus[0] - 2.0*u_cur[0] + u_minus[0];
                        const double u_diff1 = u_plus[1] - 2.0*u_cur[1] + u_minus[1];

                        rtmp[0] = dx1tx1 * u_diff0 - tx2 * (u_plus[1] - u_minus[1]);
                        rtmp[1] = dx2tx1 * u_diff1;

