        }
    }

    // Phase 3: Final scaling with better memory access pattern
    #pragma omp parallel for collapse(2) schedule(static)
    for (int j = 1; j < ny_m1; j++) {
        for (int k = 1; k < nz_m1; k++) {
            double* const rhs_slice = &rhs[1][j][k][0];
            #pragma unroll(4)
            for (int i = 1; i < nx_m1; i++) {
